interface PlayerLevelProps {
  level?: number
  pct?: number
}

export default function PlayerLevel({ level = 81, pct = 0 }: PlayerLevelProps) {
  const deg = (pct / 100) * 360
  return (
    <div className="level" data-name="Level">
      {/* Background circle */}
      <div className="level__bg" />

      {/* Number in center */}
      <div className="level__number-wrap">
        <p className="level__number">{level}</p>
      </div>

      {/* Trailing grey ring */}
      <div className="level__trailing" />

      {/* Progress ring */}
      <div
        className="level__progress"
        style={{ '--pct-deg': `${deg}deg` } as React.CSSProperties}
      >
        <div className="level__progress-inner">
          <svg
            className="level__progress-svg"
            fill="none"
            preserveAspectRatio="none"
            viewBox="0 0 40 37.3214"
          >
            <defs>
              <mask fill="white" id="level-ring-mask">
                <path d={svgPaths.ringArc} />
              </mask>
              <clipPath id="level-ring-clip">
                <path d={svgPaths.ringStroke} mask="url(#level-ring-mask)" />
              </clipPath>
            </defs>
            <path
              d={svgPaths.ringStroke}
              fill="url(#levelGrad)"
              mask="url(#level-ring-mask)"
            />
            <defs>
              <linearGradient id="levelGrad" x1="0" y1="37" x2="40" y2="0">
                <stop offset="0%" stopColor="#4b4b4b" />
                <stop offset="100%" stopColor="#fbfbfb" />
              </linearGradient>
            </defs>
          </svg>
        </div>
      </div>

      {/* Badge on top */}
      <div className="level__badge">
        <div className="level__badge-mask">
          <svg
            className="level__badge-mask-svg"
            fill="none"
            preserveAspectRatio="none"
            viewBox="0 0 22.8311 7"
          >
            <path d={svgPaths.badgeMask} fill="#121212" />
          </svg>
        </div>
        <p className="level__badge-text">LVL</p>
      </div>
    </div>
  )
}
